import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  value: 0,
  count:0,
}

export const Counter= createSlice({
  name: 'counter',
  initialState,
    reducers: {
        createCount:(state, action) => {
            const {counts} = action.payload;
            state.count=counts;
          },
    }});
    export const {createCount} = Counter.actions

export default Counter.reducer
