import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  thisMonth:0,
  dayCalculator:0,
  personPerDay:0,
  balance:0,
  monthHistory:[],
  dayHistory:[],
  balanceDetails:[],
  settings:{canCapacity:0,pricePerCan:0,familyStrength:0}
}

export const canCounter= createSlice({
  name: 'counter',
  initialState,
    reducers: {
      addWaterCan: (state, action) => {
        const { waterCapacity, price, pricePerDay } = action.payload;
        state.waterCans = [
          ...state.waterCans,
          { capacity: waterCapacity, price,perDay: pricePerDay },
        ];

        state.capacity += waterCapacity;
        state.waterprice -= price;
        state.PerDay += waterCapacity / 30;
        state.price += price;
      },

      recordPayment: (state, action) => {
        const { waterprice } = action.payload.pricePerCan;
        state.balanceDetails= [...state.balanceDetails, { waterprice }];
        state.waterprice -= waterprice;
      },

      edit: (state, action) => {
        state.settings.canCapacity = action.payload.canCapacity;
        state.settings.pricePerCan = action.payload.pricePerCan;
      },


      updateSettings: (state, action) => {
        state.settings.canCapacity = action.payload.canCapacity;
        state.settings.pricePerCan = action.payload.pricePerCan;
        state.settings.familyStrength = action.payload.familyStrength;
      },


    } 
});

// Action creators are generated for each case reducer function
export const { addWaterCan,recordPayment, edit, updateSettings} = canCounter.actions

export default canCounter.reducer
