import { Button, Grid } from "@mui/material";
import React from "react";
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import InputAdornment from '@mui/material/InputAdornment';
import PaymentsIcon from '@mui/icons-material/Payments';
import { useSelector, useDispatch } from 'react-redux';
export default function BalanceTransactions(){
  const balance = useSelector((state) => state.counter.balance);
  const [payment, setPayment] = useState(Math.abs(balance));

  const dispatch = useDispatch();
  const onHandleChange = (e) => {
    e.preventDefault();

    dispatch(recordPayment({ payment, date: new Date() }));
    setPayment(Math.abs(balance) - payment);
  };

  const transactions = useSelector((state) => state.counter.transactions);

    return(
        <Grid>
            <h1>Transactions</h1>
            <Grid>
            <FormControl fullWidth sx={{ m: 1,marginLeft:90 }}>
          <InputLabel htmlFor="outlined-adornment-amount">Amount</InputLabel>
          <OutlinedInput
            id="outlined-adornment-amount"
            startAdornment={<InputAdornment position="start"><PaymentsIcon style={{fill:"green"}}/></InputAdornment>}
            label="Amount" style={{width:500}}
          />
        </FormControl>
        <Button style={{color:"white",background:"blue"}} onClick={onHandleChange}>RECORD PAYMENT</Button>

        <List>
          {transactions?.map(({ payment, date }, i) => (
            <List.Item>
              <p>{date.toLocaleString().split(",")[0]}</p>
              <span>{payment}</span>
            </List.Item>
          ))}
        </List>
            </Grid>
        </Grid>
    )
}
